import React from 'react';
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, Legend } from 'recharts';
import { getTopProducts } from '../api/dashboard';

export default function TopProductsBarChart() {
  const data = getTopProducts();
  return (
    <div className="bg-white rounded-lg shadow p-4 mt-8">
      <h2 className="text-lg font-bold text-primary mb-2">Top Products by Category</h2>
      <ResponsiveContainer width="100%" height={260}>
        <BarChart data={data} layout="vertical" margin={{ left: 20 }}>
          <XAxis type="number" />
          <YAxis dataKey="name" type="category" />
          <Tooltip />
          <Legend />
          <Bar dataKey="sales" fill="#6366f1" name="Sales" />
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
}
