}

export function getTopProducts() {
  return [
    { category: 'Electronics', name: 'Smartphone', sales: 1200 },
    { category: 'Electronics', name: 'Laptop', sales: 900 },
    { category: 'Electronics', name: 'Headphones', sales: 700 },
    { category: 'Clothing', name: 'T-Shirt', sales: 800 },
    { category: 'Clothing', name: 'Jeans', sales: 600 },
    { category: 'Clothing', name: 'Jacket', sales: 500 },
    { category: 'Home', name: 'Blender', sales: 400 },
    { category: 'Home', name: 'Toaster', sales: 350 },
    { category: 'Home', name: 'Microwave', sales: 300 },
  ];
}
